["custom"] = "function(event, ...)\n    --mouseover functionality\n    if (event == \"UPDATE_MOUSEOVER_UNIT\" or event == \"MODIFIER_STATE_CHANGED\") then\n        local guid = UnitGUID(\"mouseover\")\n        if not guid then return end\n        local npcId = select(6, strsplit(\"-\", guid))\n        local order = aura_env.isUnitValidAndGetOrder(\"mouseover\")\n        if not npcId or not order then return end\n        aura_env.markUnit(\"mouseover\", order)\n    end\n    \n    ---------------------------------------------------------------------------------------\n    --combat marking\n    if event == \"UNIT_COMBAT\" then\n        local unit = select(1, ...)\n        local guid = UnitGUID(unit)\n        if not guid then return end\n        local npcId = select(6, strsplit(\"-\", guid))\n        local order = aura_env.isUnitValidAndGetOrder(unit)\n        if not npcId or not order or not UnitAffectingCombat(unit) or not UnitCanAttack(\"player\", unit) then return end\n        aura_env.markUnit(unit, order)\n    end \n    \n    ---------------------------------------------------------------------------------------\n    --nameplate marking\n    if event == \"NAME_PLATE_UNIT_ADDED\" then return\n        --todo\n    end\n    \n    --local unit = select(1, ...)\n    --if not aura_env.isUnitValidAndGetOrderNameplate(unit) then return end\n    --local npcId = select(6, strsplit(\"-\",UnitGUID(unit)))\n    --aura_env.markUnit(unit, aura_env.getOrder(npcId, true))\nend  \n\n\n\n",
