["init"] = {
["custom"] = "local e=aura_env\n\ne.col = '|c'..RAID_CLASS_COLORS[select(2, UnitClass(\"player\"))].colorStr--职业颜色\n\ne.HEX=function(r, g, b, a) r = r <= 1 and r >= 0 and r or 0 g = g <= 1 and g >= 0 and g or 0 b = b <= 1 and b >= 0 and b or 0 a = a <= 1 and a >= 0 and a or 0 return '|c'..string.format(\"%02x%02x%02x%02x\",a*255, r*255, g*255, b*255) end\n\ne.IC=e.HEX(e.config.IC[1],e.config.IC[2],e.config.IC[3],e.config.IC[4])--原始颜色\ne.BC=e.HEX(e.config.BC[1],e.config.BC[2],e.config.BC[3],e.config.BC[4])--变大颜色\ne.RC=e.HEX(e.config.RC[1],e.config.RC[2],e.config.RC[3],e.config.RC[4])--变小颜色\n\n\ne.va=e.config.va\nif e.va~=1 then e.va=2 end--1数字 2百份比\nif e.va~=1 then e.S='%' else e.S='' end--1数字 2百份比\n\ne.bit = e.config.bit-1--位数\n\ne.gai=e.config.gai--数值变化\n\nlocal CN if GetLocale() == \"zhCN\" then CN=true end\n\n\n--显示 文本\nfunction e:get_text()\n    if e.config.T=='Main' then--主属性\n        local t, s = {'STRENGTH', 'AGILITY', nil, 'INTELLECT'}, select(6,GetSpecializationInfo(GetSpecialization()))\n        e.T=t[s] and (_G['SPEC_FRAME_PRIMARY_STAT_'..t[s]])\n        \n    elseif e.config.T=='Power' then--能量\n        e.T=_G['POWER_TYPE_'..select(2,UnitPowerType('player'))] or POWER_TYPE_ENERGY;\n        \n    else\n        e.T=_G[e.config.T] or e.config.T;\n    end\n    \n    if e.config.de~='' then\n        if e.config.Left then\n            e.T=e.col..e.config.de..'|r'..e.T;\n        else\n            e.T=e.T..e.col..e.config.de..'|r';\n        end\n    end--添加\n    \n    if e.config.len>0 then e.T= WA_Utf8Sub(e.T,e.config.len) end--系统文本长度    \n    if e.config.class then e.T= e.col..e.T..'|r' end--使用系统颜色\nend\ne:get_text()\n\ne.MK=function(k)\n    if e.bit>0 then \n        if k>=1e6 then \n            k=string.format('%.'..e.bit..'fm', k/1e6);\n        elseif k>= 1e4 and CN then \n            k=string.format('%.'..e.bit..'fw', k/1e4);\n        elseif k>=1e3 then \n            k=string.format('%.'..e.bit..'fk', k/1e3);\n        else\n            k=string.format('%.'..e.bit..'f', k);\n        end        \n    else\n        k=string.format('%.'..e.bit..'f', k);\n    end\n    k=k..e.S;\n    return k;\nend--加k 9.1\n\n\ne.region.text2=2\n\n\ne.region.B=e.region.B or e.region:CreateFontString();\n\nlocal t=e.region.text\ne.region.B:SetFont(t:GetFont());\ne.region.B:SetFontObject(t:GetFontObject());\ne.region.B:SetIndentedWordWrap(t:GetIndentedWordWrap());\ne.region.B:SetSpacing(t:GetSpacing());\ne.region.B:SetShadowColor(t:GetShadowColor());\ne.region.B:SetShadowOffset(t:GetShadowOffset());\n\nif e.region.B:GetPoint(1) then e.region.B:ClearAllPoints() end\nif e.config.Left then\n    e.region.B:SetJustifyH('RIGHT');\n    e.region.B:SetPoint('RIGHT', e.region, 'LEFT', 0, 0)\nelse\n    e.region.B:SetJustifyH('LEFT');\n    e.region.B:SetPoint('LEFT', e.region, 'RIGHT', 0, 0)\nend\n\n\n",
