["custom"] = "-- CLEU:SPELL_AURA_APPLIED:SPELL_AURA_REMOVED:SPELL_SUMMON:UNIT_DIED:UNIT_DESTROYED:SPELL_DAMAGE,FRAME_UPDATE\n-- {master=\"string\",number=\"number\",fade=\"bool\",number2=\"number\"}\n-- SPELL_CAST_START:SPELL_CAST_SUCCESS:SPELL_CAST_FAILED:SPELL_INSTAKILL\n-- UNIT_SPELL_HASTE,PLAYER_REGEN_DISABLED,PLAYER_REGEN_ENABLED\nfunction(allstates, event, ...)\n    local e = aura_env\n    local duration = 10\n    local cooldown = 80\n    local icon = 2032221\n    local filter = \"HELPFUL\"\n    local buff = {\n        [439209] = true,\n        [434242] = true,\n    }\n    local damage = 434574\n    local explosionSpellID = 434246\n    local now = GetTime()\n    if event == \"FRAME_UPDATE\" then\n        if e.time and now - e.time < 0.5 then return end\n        e.time = now\n        e.nameplates = e.nameplates or {}\n        for _, pet in pairs(allstates) do\n            if not pet.fade then\n                local destGUID = pet.name\n                local unit = e.nameplates[destGUID]\n                if not unit or UnitGUID(unit) ~= destGUID then\n                    for i = 1, 40 do\n                        unit = \"nameplate\"..i\n                        local guid = UnitGUID(unit)\n                        -- print(unit, guid, destGUID)\n                        if not guid then break end\n                        if guid == destGUID then\n                            e.nameplates[destGUID] = unit\n                            break\n                        end\n                    end\n                end\n                if UnitGUID(unit) == destGUID then\n                    pet.number = nil\n                    for index = 1, 40 do\n                        local buffInfo = C_UnitAuras.GetAuraDataByIndex(unit, index, filter)\n                        if not buffInfo then break end\n                        if buff[buffInfo.spellId] then\n                            local tooltipInfo = {WeakAuras.GetAuraTooltipInfo(unit, index, filter)}\n                            pet.number = tooltipInfo[#tooltipInfo]\n                            break\n                        end\n                    end\n                    pet.changed = true\n                end\n            end\n        end\n        return true\n    end\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subevent, _, sourceGUID, _, _, _, destGUID, destName, _, _, spellID, spellName, _, amount = ...\n        e.pets = e.pets or {}\n        local selfGUID = UnitGUID(\"player\")\n        if selfGUID == sourceGUID and subevent == \"SPELL_SUMMON\" then\n            if spellID == 434237 then\n                allstates[sourceGUID] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    autoHide = false,\n                    duration = duration,\n                    expirationTime = now + duration,\n                    name = destGUID,\n                    master = sourceGUID,\n                    icon = icon,\n                    fade = false,\n                    damage = 0\n                }\n                e.pets[destGUID] = sourceGUID\n                e.my_pet = destGUID\n                if not e.time then e.time = now end\n                return true;\n            end\n        end\n        if subevent == \"UNIT_DIED\" or subevent == \"UNIT_DESTROYED\" then\n            --  or subevent == \"SPELL_INSTAKILL\"\n            local master = e.pets[destGUID]\n            if master then\n                allstates[master].show = false\n                e.pets[destGUID] = nil --Removes entry from map\n                e.my_pet = nil\n                return true\n            end\n        end\n        if subevent == \"SPELL_DAMAGE\" then\n            if spellID == damage then\n                if sourceGUID == e.my_pet then\n                    local master = selfGUID\n                    if allstates[master] then\n                        local prev = allstates[master].damage or 0\n                        allstates[master].damage = prev + (amount * 0.5)\n                        allstates[master].changed = true\n                        return true\n                    end\n                end\n            end\n        end\n    end\nend",
