["customText"] = "function()\n    local e=aura_env;   \n    \n    local tick=GetTime() if tick-e.tickTime < 0.4 and tick>e.tickTime then return else e.tickTime=tick end\n    \n    local m\n    \n    local isGliding, _, forwardSpeed = C_PlayerInfo.GetGlidingInfo()\n    if isGliding and forwardSpeed then\n        if forwardSpeed>0 then\n            m= format('%.0f%%', forwardSpeed*100/BASE_MOVEMENT_SPEED)\n        end\n    else\n        local unit\n        if e.config.cur then\n            unit = 'player'\n        else\n            unit = UnitExists('vehicle') and 'vehicle' or UnitOnTaxi('player') and 'player'\n        end\n        if unit then\n            local currentSpeed = GetUnitSpeed(unit)\n            if currentSpeed~=0 then\n                m= format('%.0f%%', currentSpeed*100/BASE_MOVEMENT_SPEED)\n            end\n        else\n            local _, runSpeed, flightSpeed, swimSpeed = GetUnitSpeed('player')\n            local value\n            value= IsFlying() and flightSpeed or IsSwimming() and swimSpeed or runSpeed\n            if value~=0 then\n                m=format('%.0f%%', value*100/BASE_MOVEMENT_SPEED)\n            end\n        end\n        m= m==0 and '' or m\n    end\n    \n    e.region.B:SetText(m or '')\nend\n\n\n",
