["init"] = {
["custom"] = "if InCombatLockdown() then return end\n--function DebugPrint(...) \n--    print(...)\n--end\n\nECUI = ECUI or LibStub(\"AceAddon-3.0\"):NewAddon(\"ECUI\", \"AceComm-3.0\",\"AceHook-3.0\",\"AceEvent-3.0\")\n\nlocal LibSerialize = LibStub(\"LibSerialize\")\nlocal LibDeflate = LibStub(\"LibDeflate\")\nlocal PREFIX = \"ECUI_FindCrafter\"\n\nECUI.channelName = \"FindCrafter\"\nECUI.config = aura_env.config\n\nfunction ECUI:OnInitialize()  \n    if self.initialized then return end\n    self.initialized = true\n    self:RegisterComm(PREFIX)\n    \n    self.data = CreateDataProvider(); \n    \n    C_AddOns.LoadAddOn(\"Blizzard_ProfessionsCustomerOrders\")\n    C_AddOns.LoadAddOn(\"Blizzard_Professions\")\n    C_AddOns.LoadAddOn(\"Blizzard_AuctionHouseUI\")\n    \n    C_Timer.After(tonumber(self.config.delay) or 30, function()\n            self:InitChatChannel()\n        end\n    )\n    \n    self.form = ProfessionsCustomerOrdersFrame.Form\n    \n    self:SecureHookScript(self.form, \"OnShow\") \n    self:SecureHookScript(self.form, \"OnHide\")\n    self:SecureHookScript(self.form.OrderRecipientTarget, \"OnEditFocusLost\", \"InitTarget\")\n    self:SecureHookScript(self.form.OrderRecipientTarget, \"OnShow\", \"InitTarget\") \n    self:SecureHookScript(self.form.OrderRecipientTarget, \"OnHide\", \"UpdateQuality\")\n    self:SecureHookScript(self.form.ReagentContainer.Reagents, \"OnShow\", \"AddCheckBox\")\n    self:SecureHookScript(self.form.ReagentContainer.Reagents, \"OnHide\", \"AddCheckBox\")\n    \n    self:SecureHookScript(ProfessionsFrame, \"OnShow\", \"OpenOrdersButton\")\n    self:SecureHookScript(AuctionHouseFrame.TitleContainer, \"OnShow\", \"OpenOrdersButton\")\n    self:SecureHookScript(ProfessionsFrame.CraftingPage.SchematicForm.Reagents, \"OnShow\", \"AddCheckBox\")\n    self:SecureHookScript(ProfessionsFrame.CraftingPage.SchematicForm.Reagents, \"OnHide\", \"AddCheckBox\")   \n    self:SecureHookScript(ProfessionsFrame.OrdersPage.OrderView.OrderDetails.SchematicForm.Reagents, \"OnShow\", \"AddCheckBox\")\n    self:SecureHookScript(ProfessionsFrame.OrdersPage.OrderView.OrderDetails.SchematicForm.Reagents, \"OnHide\", \"AddCheckBox\")\n    self:SecureHookScript(ProfessionsFrame.CraftingPage.SchematicForm.Details.QualityMeter,\"OnShow\", \"UpdateSkillLine\")\n    self:SecureHookScript(ProfessionsFrame.CraftingPage.SchematicForm.Details.QualityMeter,\"OnHide\", \"UpdateSkillLine\")\n    self:SecureHookScript(ProfessionsFrame.CraftingPage.SchematicForm.Details.QualityMeter,\"OnUpdate\", \"UpdateSkillLine\")\n    self:SecureHookScript(ProfessionsFrame.OrdersPage.OrderView.OrderDetails.SchematicForm.Details.QualityMeter,\"OnShow\", \"UpdateSkillLine\")\n    self:SecureHookScript(ProfessionsFrame.OrdersPage.OrderView.OrderDetails.SchematicForm.Details.QualityMeter,\"OnHide\", \"UpdateSkillLine\")\n    self:SecureHookScript(ProfessionsFrame.OrdersPage.OrderView.OrderDetails.SchematicForm.Details.QualityMeter,\"OnUpdate\", \"UpdateSkillLine\")\n    \n    print(\"|cff00ff00ECUI Loaded|r\")\nend\n\n\nfunction ECUI:InitChatChannel() \n    self.channelId = GetChannelName(self.channelName)    \n    if self.channelId == 0 then\n        JoinPermanentChannel(self.channelName, nil, nil, false)\n        self.channelId = GetChannelName(self.channelName)            \n    end\nend\n\nfunction ECUI:OnShow()\n    \n    self.data = CreateDataProvider(); \n    \n    self.selectedData = nil\n    \n    self.allocationCache = nil\n    \n    self.allockCheck = nil\n    \n    self:CreateFindButton()\n    \n    self:AddCheckBox(self.form.ReagentContainer)\n    \n    self:UpdateQuality()\n    \n    EventRegistry:RegisterCallback(\"Professions.AllocationUpdated\", function(...) self:AllocationUpdated(...) end , self);        \n    \nend\n\nfunction ECUI:OnHide() \n    \n    EventRegistry:UnregisterCallback(\"Professions.AllocationUpdated\", self);\n    \nend\nfunction ECUI:AddButtons(target) \n    self:AddCheckBox(target)\n    self:OpenOrdersButton(target)\nend\n\nfunction ECUI:OpenOrdersButton(target)\n    local name = 'ECUIOrders'..(target:GetName() or target:GetParent():GetName() or \"Default\")\n    local btn = _G[name] or \n    CreateFrame(\"Button\", name, target, \"UIPanelButtonTemplate\")\n    btn:SetFrameLevel(600)\n    btn:ClearAllPoints()\n    btn:SetSize(24,24)\n    btn:SetText(\"O\")        \n    if AuctionHouseFrame and target == AuctionHouseFrame.TitleContainer then\n        btn:SetPoint(\"TOPLEFT\", 10, 1)    \n    else\n        btn:SetPoint(\"TOPLEFT\", 90, 1)    \n    end\n    \n    if not btn.orders then\n        local frame = ProfessionsCustomerOrdersFrame\n        frame:SetMovable(true)        \n        frame:SetClampedToScreen(true)\n        frame.ignoreFramePositionManager = true\n        \n        frame:RegisterForDrag(\"LeftButton\")\n        frame:SetScript(\"OnDragStart\", function(self)\n                self:StartMoving()\n        end)\n        frame:SetScript(\"OnHide\", function(self)                \n        end)\n        frame:SetScript(\"OnDragStop\", function(self)\n                self:StopMovingOrSizing()                    \n        end)  \n        \n        btn.orders = frame\n        \n    end\n    \n    btn:SetScript(\"OnClick\", function(self) \n            self.orders:Show()\n    end)\n    \nend\n\nfunction ECUI:AddCheckBox(target)     \n    local arb = target.UnlockAllReagents or self:CreateCheckBox(target)\n    \n    arb:ClearAllPoints()\n    arb:SetChecked(false)\n    if target and target.Label and target.Label:IsVisible() then\n        arb:SetPoint(\"LEFT\", target.Label, \"LEFT\", target.Label:GetWrappedWidth(), 0)    \n        arb:Show()\n    else\n        arb:Hide()\n    end\n    \nend\n\nfunction ECUI:AllocationUpdated()\n    \n    if not self.form or not self.form.transaction then return end\n    \n    local allocation = self.form.transaction:CreateCraftingReagentInfoTbl()\n    \n    if not self.allocationCache then \n        self.allocationCache = allocation        \n    end\n    \n    if not self.compare(allocation, self.allocationCache) then \n        self.allocationCache = allocation\n        \n        if self.allockCheck then self.allockCheck:Cancel() end\n        \n        self.allockCheck = C_Timer.NewTimer (0.1, function()\n                self:InitTarget()        \n        end)\n    end        \nend\n\nfunction ECUI:Transmit(data, target)\n    DebugPrint(\"ECUI:Transmit\", data, target)\n    \n    local serialized = LibSerialize:Serialize(data)\n    local compressed = LibDeflate:CompressDeflate(serialized)\n    local encoded = LibDeflate:EncodeForWoWAddonChannel(compressed)\n    \n    if target and string.len(target) > 2 then\n        \n        self:SendCommMessage(PREFIX, encoded, \"WHISPER\", target)\n    else                \n        \n        if self.channelId and data.request and data.request.recipeID then \n            self:SendCommMessage(PREFIX, encoded, \"CHANNEL\", self.channelId) \n        end        \n    end\n    \nend\n\nfunction ECUI:OnCommReceived(prefix, payload, distribution, sender)\n    DebugPrint(\"ECUI:OnCommReceived\", distribution, sender)\n    \n    local decoded = LibDeflate:DecodeForWoWAddonChannel(payload)\n    if not decoded then return end\n    local decompressed = LibDeflate:DecompressDeflate(decoded)\n    if not decompressed then return end\n    local success, data = LibSerialize:Deserialize(decompressed)\n    if not success then return end\n    \n    DebugPrint(\"Decoded data:\", data)\n    \n    if data.response and sender and string.len(sender) > 2 then  \n        self:InsertData({sender = sender, details = data.response})\n        self:UpdateQuality()\n    end\n    \n    if data.request and data.request.recipeID then  \n        DebugPrint(\"check recipe:\",data.request.recipeID)\n        \n        ProfessionsFrame.CraftingPage:OnLoad()\n        \n        local isLearned = C_TradeSkillUI.IsRecipeProfessionLearned(data.request.recipeID) \n        local recipeInfo = C_TradeSkillUI.GetRecipeInfo(data.request.recipeID)        \n        \n        if isLearned then\n            \n            local response = C_TradeSkillUI.GetCraftingOperationInfo(data.request.recipeID, \n                data.request.reagents or {})\n            \n            if not response or response.baseSkill == 0 then \n                DebugPrint(\"Bad Response:\", response)            \n                return \n            end\n            \n            response.learned = (not recipeInfo and \"notloaded\" ) or (not recipeInfo.learned and \"notlearned\")\n            \n            self:Transmit({response = response}, sender) \n        else\n            DebugPrint(\"Recipe \", data.request.recipeID, \"Learned:\", isLearned, \"info:\", recipeInfo and recipeInfo.learned)\n        end\n    end   \n    \nend\n\nfunction ECUI:InsertData(data) \n    if not self.data then \n        self.data = CreateDataProvider(); \n    end\n    self.data:RemoveByPredicate(function(el) return el.sender and data.sender and el.sender == data.sender end)\n    self.data:Insert(data)\nend\n\n\nfunction ECUI:InitTarget()\n    local target = self.form.OrderRecipientTarget:GetText()\n    if target and string.len(target) > 3 then\n        \n        self:QueryRecipe(target)        \n    end\nend\n\nfunction ECUI:QueryRecipe(target)\n    local trans = self.form.transaction\n    if not trans or not trans.recipeID then return end\n    \n    self:Transmit({\n            request = {                \n                recipeID = trans.recipeID,\n                reagents = trans:CreateCraftingReagentInfoTbl() or {}\n            }\n    }, target)    \nend\n\nfunction ECUI:FindCrafter_OnClick(btn, ...)\n    DebugPrint(\"ECUI:FindCrafter_OnClick\", btn, ...)\n    if self.channelId == 0 then \n        self:InitChatChannel()\n    else\n        self.data:Flush()\n        self:QueryRecipe()    \n        self:PopupList(btn)        \n    end\nend\n\nfunction ECUI:SelectCrafter(data)\n    self.selectedData = data\n    if self.form and self.form.OrderRecipientTarget then\n        \n        self.form.OrderRecipientTarget:SetText(data.sender) \n        self:UpdateQuality()\n    end\nend\n\nfunction ECUI:CreateFindButton()\n    local form = self.form\n    local btn = ECUIFindCrafterBtn or \n    CreateFrame(\"Button\", \"ECUIFindCrafterBtn\", form.OrderRecipientTarget, \"UIPanelButtonTemplate\")\n    \n    btn:SetSize(80,22)\n    btn:SetTextToFit(\"Find\")\n    btn:SetPoint(\"TOPRIGHT\", form.OrderRecipientTarget, \"TOPLEFT\", -31, 0)    \n    btn:SetScript(\"OnClick\", function(btn, ...) ECUI:FindCrafter_OnClick(btn, ...) end)    \n    btn:SetScript(\"OnUpdate\", function(btn)\n            if self then\n                self.channelId = GetChannelName(self.channelName)    \n                if self.channelId == 0 then \n                    btn:SetTextToFit(\"Join\")\n                else\n                    btn:SetTextToFit(\"Find\")\n                end\n            end            \n    end)\n    self.FindButton = btn\n    \nend\n\nfunction ECUI:UpdateQuality()\n    local form = ProfessionsCustomerOrdersFrame.Form\n    if not form:IsVisible() or not form.transaction then \n        return\n    end\n    local recipeID = form.transaction.recipeID        \n    local target = form.OrderRecipientTarget\n    local value = target and target:GetText()\n    \n    if target and target:IsVisible() and value and recipeID and string.len(value) > 2 then\n        \n        local function check(el) \n            return el and el.sender and el.details and value and recipeID and el.sender == value and el.details.recipeID == recipeID \n        end\n        \n        local index, data = self.data:FindByPredicate(check)\n        \n        if check(data) then            \n            \n            form:SetMinimumQualityIndex(data.details.craftingQuality);\n            form:UpdateMinimumQuality()            \n            form.MinimumQuality.Dropdown:Hide()\n            \n            local label = form.MinimumQuality\n            label.Text:ClearAllPoints()\n            label.Text:SetPoint(\"RIGHT\",label.Dropdown,\"LEFT\", -25,0)\n            \n            local result = MinQualityResult or CreateFrame(\"Frame\", \"MinQualityResult\", label)\n            result:ClearAllPoints()\n            result:SetPoint(\"LEFT\", label.Text, \"RIGHT\", 0, 0)\n            result:SetSize(100,40)\n            result:Show()\n            \n            local quality = MinQualityQuality or result:CreateFontString(\"MinQualityQuality\",\"ARTWORK\", \"GameFontNormal\")    \n            quality:ClearAllPoints()\n            quality:SetPoint(\"TOPLEFT\", result,\"TOPLEFT\", 0, -8)        \n            quality:SetText(self.FormatResponse(data, true))\n            quality:Show()\n            \n            local skill = MinQualitySkill or result:CreateFontString(\"MinQualitySkill\", \"ARTWORK\", \"GameFontNormal\")    \n            skill:SetText(format(\"Skill: %d/%d\", \n                    data.details.baseSkill + data.details.bonusSkill,                    \n                    data.details.baseDifficulty + data.details.bonusDifficulty\n                )\n            )\n            skill:SetTextColor(GRAY_FONT_COLOR.r,GRAY_FONT_COLOR.g,GRAY_FONT_COLOR.b)\n            skill:ClearAllPoints()\n            skill:SetPoint(\"TOPLEFT\", result, \"TOPLEFT\", 2, -32)        \n            skill:SetScale(0.7)\n            skill:Show()                        \n            \n            return \n        end        \n    end\n    \n    form:UpdateMinimumQualityAnchor()\n    form:UpdateMinimumQuality()\n    form.MinimumQuality.Dropdown:Show()\n    if MinQualityResult then         \n        MinQualityResult:Hide() \n    end\n    \nend\n\nfunction ECUI:PopupList(btn)\n    local popup = ECUIPopupList or CreateFrame(\"Frame\", \"ECUIPopupList\", btn:GetParent(), \"TooltipBackdropTemplate\")\n    local box = ECUIPopupListScrollBox or CreateFrame(\"Frame\", \"$parentScrollBox\", popup, \"WowScrollBoxList\")\n    local scroll = ECUIPopupListScrollBar or CreateFrame(\"EventFrame\", \"$parentScrollBar\", popup, \"MinimalScrollBar\")\n    \n    popup:SetFrameStrata(\"TOOLTIP\")\n    popup:SetPoint(\"TOPLEFT\", btn, \"TOPRIGHT\", 5, 0)\n    popup:SetSize(250, 100)\n    popup:RegisterEvent(\"GLOBAL_MOUSE_DOWN\")\n    popup:SetScript(\"OnEvent\", function(self,event,...) \n            if event == \"GLOBAL_MOUSE_DOWN\" then\n                \n                local buttonName = ...;\n                local isRightButton = buttonName == \"RightButton\";\n                \n                local mouseFocus = GetMouseFoci()[1];\n                if not isRightButton and DoesAncestryInclude(self.owner, mouseFocus) then\n                    return;\n                end\n                \n                if isRightButton or (not DoesAncestryInclude(self, mouseFocus) and mouseFocus ~= self) then\n                    \n                    self:Hide()\n                end\n            end\n    end)\n    \n    box:SetPoint(\"TOPLEFT\", 2, -4)\n    box:SetPoint(\"BOTTOMRIGHT\", 2, 4)    \n    \n    scroll:SetPoint(\"TOPLEFT\", box, \"TOPRIGHT\", 0, 0)\n    scroll:SetPoint(\"BOTTOMLEFT\", box, \"BOTTOMRIGHT\", 0, 0)\n    \n    local view = CreateScrollBoxListLinearView(4,4,4,4);\n    \n    view:SetElementExtent(20)\n    \n    view:SetDataProvider(self.data);\n    \n    local initializer = function(button, elementData)\n        if not button.init then \n            button.init = true\n            button.fs = button:CreateFontString(nil, \"OVERLAY\", \"GameTooltipText\")\n            button.fs:SetPoint(\"LEFT\", 4, 0)\n            button.fs:SetFontObject(\"GameFontNormal\")\n            button.fs:SetTextColor(1, 1, 1)\n            button:SetHighlightTexture(\"auctionhouse-ui-row-highlight\")\n            button:GetHighlightTexture():SetBlendMode(\"ADD\")\n            button:GetHighlightTexture():SetAllPoints()\n            button:SetScript(\"OnClick\", function(btn) \n                    popup:SetSelectedListIndex(btn.listIndex)\n            end) \n        end\n        button.fs:SetText(ECUI.FormatResponse(elementData))\n        button.listIndex = elementData\n    end\n    \n    view:SetElementInitializer(\"Button\", initializer);\n    \n    ScrollUtil.InitScrollBoxWithScrollBar(box, scroll, view);        \n    \n    popup.ecui = self\n    function popup:SetSelectedListIndex(...)\n        self.ecui:SelectCrafter(...)\n        self:Hide()\n    end\n    \n    popup:Show()\nend\n\nfunction ECUI:CreateCheckBox(target)\n    if not target then return end\n    local arb = CreateFrame(\"CheckButton\", nil, target, \"UICheckButtonTemplate\")\n    \n    target.UnlockAllReagents = arb\n    \n    arb:ClearAllPoints()\n    arb:SetSize(20,20)\n    arb.text:SetText(LIGHTGRAY_FONT_COLOR:WrapTextInColorCode(\"全部解锁\"))\n    arb:SetChecked(false)    \n    arb:Hide()\n    \n    function arb:Update() \n        \n        if self:GetChecked() then\n            ECUI:RawHook(ItemUtil, \"GetCraftingReagentCount\", \"GetCraftingReagentCountOverride\",true)            \n        else\n            ECUI:Unhook(ItemUtil, \"GetCraftingReagentCount\")\n        end\n        \n        local sf = self:GetParent()\n        \n        if sf then \n            if ProfessionsFrame.OrdersPage.OrderView:IsVisible() then\n                ProfessionsFrame.OrdersPage.OrderView:OnEvent(\"BAG_UPDATE\")\n                \n            elseif ProfessionsCustomerOrdersFrame.Form:IsVisible() then    \n                ProfessionsCustomerOrdersFrame.Form:OnEvent(\"BAG_UPDATE\")\n                \n            else\n                ProfessionsFrame.CraftingPage.SchematicForm:UpdateAllSlots()\n            end\n            \n            local qd = sf.QualityDialog\n            if qd and qd.recipeID then \n                qd:Setup() \n            end        \n        end\n        \n    end\n    \n    arb:SetScript(\"OnClick\", function(self) self:Update() end)\n    \n    arb:SetScript(\"OnHide\", function() ECUI:Unhook(ItemUtil, \"GetCraftingReagentCount\") end)\n    \n    return arb\nend\n\nfunction ECUI:UpdateSkillLine(frame)                \n    if not frame or not frame.operationInfo or not frame.statLinePool then frame = frame:GetParent() end\n    \n    if not frame or not frame.operationInfo or not frame.statLinePool then return end\n    \n    for statLine,_ in frame.statLinePool:EnumerateActive() do                    \n        if statLine.LeftLabel:GetText() == PROFESSIONS_CRAFTING_STAT_TT_CRIT_HEADER then                         \n            \n            local text = ECUI.FormatDetails(frame.operationInfo)            \n            \n            statLine.RightLabel:SetText(text)\n            break\n        end            \n    end\nend\n\nfunction ECUI:GetCraftingReagentCountOverride(itemId) \n    \n    return 9999\nend\n\n\nfunction ECUI.FormatResponse(data, hideSender) \n    local sender = data.sender    \n    local details = data.details\n    local skill = \"|cFF999999???|r\"\n    \n    if details then     \n        skill = ECUI.FormatDetails(details) \n    end    \n    \n    return sender and not hideSender and format(\"%s - %s\", sender, skill) or skill\nend\n\nfunction ECUI.FormatDetails(details) \n    \n    if not details then return end\n    if details.learned == \"notloaded\" then return \"|cff999999not loaded|r\" end\n    if details.learned == \"notlearned\" then return \"|cffff0000not learned|r\" end\n    if not details.isQualityCraft then return \"|cff00ff00ok|r\" end\n    \n    local skill = details.baseSkill + details.bonusSkill\n    local difficulty = details.baseDifficulty + details.bonusDifficulty\n    local bonus, pct\n    \n    for _,stat in pairs(details.bonusStats) do \n        if  stat.bonusStatName == PROFESSIONS_CRAFTING_STAT_TT_CRIT_HEADER then\n            pct, bonus = string.match(stat.ratingDescription, \"([0-9]+\\.?[0-9]+)%%[^0-9]+([0-9]+)\")\n            break;\n        end\n    end\n    \n    if not (pct and bonus and skill) then return end\n    \n    local diff = skill - details.upperSkillTreshold + (skill < details.upperSkillTreshold and bonus or 0)\n    \n    local baseQuality = details.craftingQuality                \n    local procQuality = min(details.guaranteedCraftingQualityID <= 3 and 3 or 5, baseQuality + (diff >= 0 and 1 or 0))\n    \n    local icon = format(\"|A:%s:16:16|a\", Professions.GetIconForQuality(procQuality, true))\n    local icon_next = format(\"|A:%s:16:16|a\", Professions.GetIconForQuality(min(5, procQuality + 1), true))\n    \n    \n    local diffText = (diff < 0 and format(\"(|cffff0000%d|r to %s)\", diff, icon_next))\n    or (diff >= 0 and format(\"(|cff00ff00+%d|r)\", diff))\n    \n    local text = (baseQuality == procQuality and format(\"%s%s\", icon, diffText)) \n    or format(\"%.1f%% to %s%s\", pct, icon, diffText)\n    \n    return text  \nend\n\nfunction ECUI.compare(t1,t2,ignore_mt)\n    local ty1 = type(t1)\n    local ty2 = type(t2)\n    if ty1 ~= ty2 then return false end\n    -- non-table types can be directly compared\n    if ty1 ~= 'table' and ty2 ~= 'table' then return t1 == t2 end\n    -- as well as tables which have the metamethod __eq\n    local mt = getmetatable(t1)\n    if not ignore_mt and mt and mt.__eq then return t1 == t2 end\n    for k1,v1 in pairs(t1) do\n        local v2 = t2[k1]\n        if v2 == nil or not ECUI.compare(v1,v2) then return false end\n    end\n    for k2,v2 in pairs(t2) do\n        local v1 = t1[k2]\n        if v1 == nil or not ECUI.compare(v1,v2) then return false end\n    end\n    return true\nend\n\nECUI:OnInitialize()",
