["start"] = {
["custom"] = "local e = aura_env\nlocal r = e.region\nlocal target_frame = e.state.dest_frame\nlocal height = e.state.dest_frame_height\nr:ClearAllPoints()\nr:SetAllPoints(target_frame)\ne:StartAnimation(r, height)",
