["init"] = {
["custom"] = "local e = aura_env\nlocal r = e.region\nlocal LGF = LibStub(\"LibGetFrame-1.0\")\n\nfunction e.GetFrame(guid)\n    for unit in WA_IterateGroupMembers() do\n        if UnitGUID(unit) == guid then return LGF.GetFrame(unit) end\n    end\n    return nil\nend\n\nfunction e.Throttle(amount,overhealing,limit)\n    local amount, overhealing = amount or 0, overhealing or 0\n    if amount > limit then return true else return false end\nend\n\nfunction e:InBlackList(spellId,spellName)\n    local spellId = tostring(spellId)\n    local spellName = tostring(spellName)\n    return string.find(self.config.blacklist,spellId) or string.find(self.config.blacklist,spellName)\nend\n\nfunction e:StartAnimation(r, height)\n    local duration = e.config.duration or 1\n    duration = duration + 0.1\n    local height = height or 40\n    local animation_group = r:CreateAnimationGroup()\n    local alpha = animation_group:CreateAnimation(\"Alpha\")\n    alpha:SetFromAlpha(1)\n    alpha:SetToAlpha(0)\n    alpha:SetDuration(duration)\n    local translation = animation_group:CreateAnimation(\"Translation\")\n    translation:SetOffset(0, height)\n    translation:SetDuration(duration)\n    animation_group:Play()\nend\n\nfunction e:StopAnimation(r)\n    r:StopAnimating()\nend",
["do_custom"] = true,
},
