["custom"] = "function ( allstates, event, ... )\n    local e = aura_env\n    local timestamp,sub_event,hideCaster,sourceGUID,sourceName,sourceFlags,sourceRaidFlags,destGUID,destName,destFlags,destRaidFlags,spellId,spellName,spellSchool,amount,overhealing,absorbed,critical = ...\n    if e:InBlackList(spellId,spellName) then return false end\n    if sourceGUID ~= UnitGUID(\"player\") then return false end\n    if sub_event == \"SPELL_HEAL\" then\n        if not e.config.direct_heal then return false end\n        local limit = e.config.direct_heal_min\n        if not e.Throttle(amount,overhealing,limit) then return false end\n    elseif sub_event == \"SPELL_PERIODIC_HEAL\" then\n        if not e.config.periodic_heal then return false end\n        local limit = e.config.periodic_heal_min\n        if not e.Throttle(amount,overhealing,limit) then return false end\n    end\n    local effective_amount = amount and overhealing and amount - overhealing\n    local dest_frame = e.GetFrame(destGUID)\n    if not dest_frame then return false end\n    local state = {\n        show = true,\n        changed = true,\n        progressType = \"timed\",\n        duration = e.config.duration,\n        expirationTime = GetTime() + e.config.duration,\n        autoHide = true,\n        dest_frame = dest_frame,\n        dest_frame_height = dest_frame:GetHeight(),\n        amount = FormatLargeNumber(amount),\n        effective_amount = FormatLargeNumber(effective_amount),\n        critical = critical\n    }\n    allstates[#allstates+1] = state\n    return true\nend\n",
